INSERT INTO `jieqi_system_blocks` (`bid`, `blockname`, `modname`, `filename`, `classname`, `side`, `title`, `description`, `content`, `vars`, `template`, `cachetime`, `contenttype`, `weight`, `showstatus`, `custom`, `canedit`, `publish`, `hasvars`) VALUES(0, '电子书列表区块', 'obook', 'block_obooklist', 'BlockObookObooklist', 0, '最近更新', '&nbsp;&nbsp;&nbsp;&nbsp;本区块允许用户自定义模板和参数，并且不同的设置可以保存成不同的区块。<br>&nbsp;&nbsp;&nbsp;&nbsp;区块默认模板文件为“block_obooklist.html”，在/modules/obook/templates/blocks目录下，如果您定义了另外模板文件，也必须在此目录。模板文件设置留空表示使用默认模板。<br>&nbsp;&nbsp;&nbsp;&nbsp;区块允许设置六个参数，不同参数之间用英文逗号分割“,”。<br>&nbsp;&nbsp;&nbsp;&nbsp;参数一是排行方式（默认按最近更新），允许以下几种设置：1、“lastupdate” - 按最近更新；2、“postdate” - 按最新入库；3、“toptime” - 按本站推荐；4、“goodnum” - 按收藏数量；5、“allsale” - 按总销售量；6、“monthsale” - 按月销售量；7、“weeksale” - 按周销售量；8、“daysale” - 按日销售量；9、“size” - 按电子书字数；<br>&nbsp;&nbsp;&nbsp;&nbsp;参数二是显示行数，使用整数（默认 15）<br>&nbsp;&nbsp;&nbsp;&nbsp;参数三是电子书类别（默认 0 表示所有类别），此处使用得是类别序号而不是名称，比如“玄幻小说”类别序号是 3 ，这里就设置成 3，如果要同时选择多个类别，可以用“|”分割，比如 3|4|7<br>&nbsp;&nbsp;&nbsp;&nbsp;参数四是出版商（默认留空表示全部出版商），此处使用得是出版商序号而不是名称，多个序号可以用“|”分隔<br>&nbsp;&nbsp;&nbsp;&nbsp;参数五是指是否全本（默认 0 表示不判断），1 表示只显示全本作品，2 表示连载作品<br>&nbsp;&nbsp;&nbsp;&nbsp;参数六是指显示顺序（默认 0 表示按从大到小排序），1 表示从小到大排序。<br>&nbsp;&nbsp;&nbsp;&nbsp;参数设置中一项或者多项留空均表示使用默认值。例子： “lastupdate,,0,,0,0” 表示显示15条最近更新的作品，其中第二个参数留空，所以使用默认的15条。', '', 'lastupdate,15,0,0,0,0', 'block_obooklist.html', 0, 1, 50100, 0, 0, 0, 0, 1);
